tal:block.script:
jQuery.noConflict()
jQuery(document).ready( function () {
    jQuery("#change-message-notification-email-content").load("/help/manual/ #groupserver-changing-your-message-notification-settings-instructions", {}, fix_links);
    jQuery("#change-message-notification-web-content").load("/help/manual/ #groupserver-changing-your-message-notification-settings-instructions", {}, fix_links);
    jQuery("#reading-web-content").load("/help/manual/ #groupserver-reading-messages-using-the-web-instructions", {}, fix_links);
});
var fix_links = function (responseText, textStatus, XMLHttpRequest) {
  jQuery(this).find("a").each(function(){jQuery(this).attr('href', '/help/manual'+jQuery(this).attr('href'));});
}
